import "../../styles/components/roundchart.css"
import { RadialBarChart , RadialBar , ResponsiveContainer , PolarAngleAxis , LabelList} from "recharts"

function RoundChart ({input}) {
    let achievement = (input.score)*100
    let tab = [{score:achievement,fill:"red"}]

    return (
        <ResponsiveContainer width="30%" height={300}>
            <RadialBarChart innerRadius="50%" outerRadius="80%" barSize={10} data={tab} startAngle={90} endAngle={450}>
                <PolarAngleAxis type="number" domain={[0, 100]} dataKey="score" angleAxisId={0} tick={false}/>
                <RadialBar minAngle={15} clockWise={true} dataKey="score" angleAxisId={0} background={true} label={{position:"center",content:<CustomLabel value={achievement}/>}}/>
            </RadialBarChart>
        </ResponsiveContainer>
    )
}

function CustomLabel ({viewBox,value}) {
    const {cx , cy} = viewBox
    return (
        <text x={cx} y={cy} className="round_label" textAnchor="middle" dominantBaseline={"central"}>
            <span>{value}</span>
            <span>de votre objectif</span>
        </text>
    )
}

export default RoundChart
