import "../../styles/components/card.css"

function Card ({value,category}) {

    let icon = ""
    let unit = ""

switch (category) {
    case "calories":
        icon = "calorie"
        unit = "kCal"
        break;
    case "proteines":
        icon = "proteine"
        unit = "g"
        break;
    case "glucides":
        icon = "glucide"
        unit = "g"
        break;
    case "lipides":
        icon = "lipide"
        unit = "g"
        break;
    default:
        break;
}

    return (
        <div className="card_body">
            <img className="card_icon" src={"../../public/assets/" + icon +".png"} alt="" />
            <div className="card_text">
                <span className="card_value">{value}{unit}</span>
                <span className="card_unit">{category}</span>
            </div>
        </div>
    )
}

export default Card
