import "../../styles/components/roundchart.css"
import { RadialBarChart , RadialBar , ResponsiveContainer , PolarAngleAxis , Label} from "recharts"

function RoundLabel ({test}) {
    return (
        <text className="round_label" x={100} y={100}>
            <span className="round_achievement">{test}%</span>
            <span className="round_text_filler">de votre objectif</span>
        </text>
    )
}

function RoundChart ({input}) {
    let achievement = (input.score)*100
    let tab = [{score:achievement,fill:"red"}]

    return (
        <ResponsiveContainer width="30%" height={300}>
            <RadialBarChart innerRadius="50%" outerRadius="80%" barSize={10} data={tab} startAngle={90} endAngle={450}>
                <PolarAngleAxis type="number" domain={[0, 100]} dataKey="score" angleAxisId={0} tick={false}/>
                <RadialBar minAngle={15} clockWise={true} dataKey="score" angleAxisId={0} background={true} label={<RoundLabel test={20}/>}/>
            </RadialBarChart>
        </ResponsiveContainer>
    )
}

export default RoundChart
