import "../../styles/components/radchart.css"
import { Radar, RadarChart,PolarGrid,PolarAngleAxis ,ResponsiveContainer } from "recharts"

function RadChart ({input}) {

    const formatTicks = (tick) => {
        let result = "" + input.kind[tick]
        return result[0].toUpperCase() + result.slice(1)
    }

    const positionTicks = ({ payload , x , y , cx , cy , ...rest}) => {
        let result = "" + input.kind[payload]
        
        return(
            <text
            {...rest}
                y={y + (y - cy) / 3}
                x={x + (x - cx) / 3}
            >
            {result[0].toUpperCase() + result.slice(1)}
            </text>
        )
    }

    return (
        <ResponsiveContainer className="rad_body" width="30%" height={300}>
            <RadarChart cx="50%" cy="50%" outerRadius="80%" data={input.data} margin={{top:10,right:40,left:40,bottom:10}}>
                <PolarGrid radialLines={false}/>
                <PolarAngleAxis dataKey="kind" tickFormatter={formatTicks} tickLine={false} tick={positionTicks} stroke="white"/>
                <Radar dataKey="value" stroke="#FF0101B2" fill="#FF0101B2"/>
            </RadarChart>
        </ResponsiveContainer>
    )
}

export default RadChart
