import "../../styles/components/lines.css"
import { LineChart, Line , XAxis , YAxis , Tooltip ,ResponsiveContainer} from "recharts";

function Lines ({input}) {

    const formatTicks = (tick) => {
        switch (tick) {
            case 1:
                return "L"
            case 2:
                return "M"
            case 3:
                return "M"
            case 4:
                return "J"
            case 5:
                return "V"
            case 6:
                return "S"
            case 7:
                return "D"
            default:
                break;
        }
    }

    const customTooltip = ({active , payload}) => {
        if (active && payload && payload.length) {
            console.log(payload)
            return (
                <div className="lines_tooltip">
                    <p>{payload[0].value + " min"}</p>
                </div>
            )
        }
    }

    return input && (
        <ResponsiveContainer className="lines_body" width={260} height={300}>
            <LineChart data={input}>
                <XAxis dataKey="day" tickFormatter={formatTicks} width="100%"/>
                <YAxis dataKey="sessionLength" hide={true}/>
                <Tooltip content={customTooltip}/>
                <Line type="monotone" dataKey="sessionLength" stroke="white" strokeWidth={3} dot={false} activeDot={{stroke:'white',strokeWidth:2,r:5}} />
            </LineChart>
        </ResponsiveContainer>
    )
}

export default Lines
